import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import Room from "../components/Room";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";
import rooms from "../rooms.json";


class Rooms extends Component {
  state = {
    rooms,
    title: "",
    author: "",
    synopsis: ""
  };

  componentDidMount() {
    this.loadRooms();
  }

  loadRooms = () => {
    API.getRooms()
      .then(res =>
        this.setState({ rooms: res.data, title: "", author: "", synopsis: "" })
      )
      .catch(err => console.log(err));
  };

  deleteRoom = id => {
    API.deleteRoom(id)
      .then(res => this.loadRooms())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.title && this.state.author) {
      API.saveRoom({
        title: this.state.title,
        author: this.state.author,
        synopsis: this.state.synopsis
      })
        .then(res => this.loadRooms())
        .catch(err => console.log(err));
    }
  };

  render() {
    return (
      <Container fluid>
        <Jumbotron>
          <h1>Pick A Room!</h1>
        </Jumbotron>

        {/* Room Display */}
        {this.state.rooms.map(room => (
          <Link to={"/rooms/" + room.id}>
            <Room
              cardClicked={this.cardClicked}
              id={room.id}
              key={room.id}
              image={room.image}
            />
          </Link>
        ))}


        <form>
          <Input
            value={this.state.title}
            onChange={this.handleInputChange}
            name="title"
            placeholder="Chore (required)"
          />
          <Input
            value={this.state.author}
            onChange={this.handleInputChange}
            name="author"
            placeholder="Owner (required)"
          />
          <TextArea
            value={this.state.synopsis}
            onChange={this.handleInputChange}
            name="synopsis"
            placeholder="Synopsis (Optional)"
          />
          <FormBtn
            disabled={!(this.state.author && this.state.title)}
            onClick={this.handleFormSubmit}
          >
            Add Chore!
              </FormBtn>
        </form>

        <Jumbotron>
          <h1>Chores to Complete</h1>
        </Jumbotron>
        {/* Chore list begins */}
        {this.state.rooms.length ? (
          <List>
            {this.state.rooms.map(room => (
              <ListItem key={room._id}>
                <Link to={"/books/" + room._id}>
                  <strong>
                    {room.title} by {room.author}
                  </strong>
                </Link>
                <DeleteBtn onClick={() => this.deleteRoom(room._id)} />
              </ListItem>
            ))}
          </List>
        ) : (
            <h3>No Results to Display</h3>
          )}
      </Container>
    );
  }
}

export default Rooms;
