import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Container } from "../components/Grid";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import moment from 'moment';


class Description extends Component {
  state = {
    task: {}
  };
  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc
  componentDidMount() {
    API.getTask(this.props.match.params.id)
      .then(res => this.setState({ task: res.data }))
      .catch(err => console.log(err));
  }

  render() {
    return (
      <Container fluid>
        <Jumbotron>
          <h1>
            {this.state.task.title}
          </h1>
        </Jumbotron>

        <strong>
          <div class="container">
            <div class="row">
              Needs to be completed {moment(this.state.task.date).startOf('day').fromNow()}
            </div>
            <div class="row">
              {this.state.task.owner}
            </div>
            <div class="row">
              {this.state.task.description}
            </div>
          </div>
        </strong>
        <Link to={"/rooms/" + this.state.task.roomId}>‚Üê Back to Tasks</Link>
      </Container>
    );
  }
}

export default Description;
